// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * @title BitcoinUpDown
 * @dev A betting game where players predict whether the price of Bitcoin will go up or down within a round duration.
 */
contract BitcoinUpDown is Ownable, ReentrancyGuard {
    enum UP_DOWN {
        UP,
        DOWN
    }

    struct Player {
        uint256 bet;
        UP_DOWN up_down;
        uint256 round;
    }

    struct Round {
        uint256 totalPrizePool;
        uint256 upPrizePool;
        uint256 downPrizePool;
        UP_DOWN winner;
    }

    mapping(address => Player) public players;
    mapping(uint256 => Round) public rounds;
    AggregatorV3Interface public immutable btcUsdPriceFeed;

    uint256 public round;
    uint256 public tsStart;
    uint256 public fee;
    address private immutable dev;

    int256 public priceOpen;
    int256 public priceClose;

    // Events
    event StartRound(
        uint256 indexed round,
        uint256 indexed tsStart,
        int256 priceOpen,
        uint256 seed
    );
    event BetPlaced(
        uint256 indexed round,
        address indexed player,
        uint256 amount,
        UP_DOWN up_down
    );
    event EndRound(
        uint256 indexed round,
        uint256 indexed tsClose,
        int256 priceClose,
        uint256 prizePool,
        uint256 upPool,
        uint256 downPool,
        UP_DOWN winner
    );
    event PrizePoolAdded(
        uint256 indexed round,
        address indexed from,
        uint256 amount
    );

    constructor(address _priceFeedAddress) {
        require(_priceFeedAddress != address(0), "Invalid price feed address");
        dev = msg.sender;
        btcUsdPriceFeed = AggregatorV3Interface(_priceFeedAddress);
        _startRound(0); // Initialize the first round with no seed
    }

    /**
     * @notice Get the latest price of Bitcoin in USD
     * @return The latest price in int256 format
     */
    function getPrice() public view returns (int256) {
        (, int256 price, , , ) = btcUsdPriceFeed.latestRoundData();
        return price;
    }

    /**
     * @notice Adds Ether to the current round's prize pool.
     */
    function addToPrizePool() public payable {
        require(msg.value > 0, "Minimum amount to add is 1 wei");
        rounds[round].totalPrizePool += msg.value;
        emit PrizePoolAdded(round, msg.sender, msg.value);
    }

    /**
     * @notice Place a bet for the current round.
     * @param _up_down The direction of the bet (UP or DOWN).
     */
    function bet(UP_DOWN _up_down) public payable {
        require(msg.value > 0, "Minimum bet is 1 wei");
        require(block.timestamp - tsStart <= 3600, "Betting is closed");

        Player storage player = players[msg.sender];
        Round storage currentRound = rounds[round];

        // Ensure the player has no pending bets or previous round obligations
        require(
            player.bet == 0 || player.round != round,
            "Bet already placed this round"
        );
        require(
            player.bet == 0 || player.up_down != currentRound.winner,
            "Pending withdrawal from previous round"
        );

        // Update player and round data
        player.bet = msg.value;
        player.up_down = _up_down;
        player.round = round;

        currentRound.totalPrizePool += msg.value;
        if (_up_down == UP_DOWN.UP) {
            currentRound.upPrizePool += msg.value;
        } else {
            currentRound.downPrizePool += msg.value;
        }

        emit BetPlaced(round, msg.sender, msg.value, _up_down);
    }

    /**
     * @notice Withdraw winnings for a player if they won in a previous round.
     */
    function withdraw() public nonReentrant {
        Player storage player = players[msg.sender];
        require(player.bet > 0, "No active bets");
        require(player.round < round, "Current round still ongoing");

        Round storage playerRound = rounds[player.round];
        require(player.up_down == playerRound.winner, "Not a winning bet");

        uint256 prize = _calculatePrize(player, playerRound);
        player.bet = 0; // Reset player bet
        (bool sent, ) = msg.sender.call{value: prize}("");
        require(sent, "Withdrawal failed");
    }

    /**
     * @notice Withdraw accumulated fees for the developer.
     */
    function withdrawFee() public nonReentrant {
        require(msg.sender == dev, "Only dev can withdraw fees");
        require(fee > 0, "No fees available for withdrawal");
        uint256 devFee = fee;
        fee = 0; // Reset fee
        (bool sent, ) = dev.call{value: devFee}("");
        require(sent, "Fee withdrawal failed");
    }

    /**
     * @notice End the current round and start a new one.
     */
    function endRound() public {
        require(
            block.timestamp - tsStart >= 1 days,
            "Round duration is less than 24 hours"
        );

        Round storage currentRound = rounds[round];
        priceClose = getPrice();

        // Deduct dev fee (1%)
        uint256 devTax = currentRound.totalPrizePool / 100;
        fee += devTax;
        currentRound.totalPrizePool -= devTax;

        // Determine the winner
        UP_DOWN winner;
        uint256 seed = 0;

        if (priceClose >= priceOpen) {
            winner = UP_DOWN.UP;
            if (currentRound.upPrizePool == 0) {
                seed = currentRound.totalPrizePool; // Carry forward if no UP bets
            }
        } else {
            winner = UP_DOWN.DOWN;
            if (currentRound.downPrizePool == 0) {
                seed = currentRound.totalPrizePool; // Carry forward if no DOWN bets
            }
        }
        currentRound.winner = winner;

        emit EndRound(
            round,
            block.timestamp,
            priceClose,
            currentRound.totalPrizePool,
            currentRound.upPrizePool,
            currentRound.downPrizePool,
            winner
        );

        // Start a new round
        round += 1;
        _startRound(seed);
    }

    /**
     * @dev Internal function to start a new round.
     * @param seed The amount to seed the new round's prize pool.
     */
    function _startRound(uint256 seed) private {
        Round storage newRound = rounds[round];
        newRound.totalPrizePool = seed;
        priceOpen = getPrice();
        tsStart = block.timestamp;
        emit StartRound(round, tsStart, priceOpen, seed);
    }

    /**
     * @dev Internal function to calculate prize for a player.
     * @param player The player's data.
     * @param playerRound The round data associated with the player.
     * @return The calculated prize amount.
     */
    function _calculatePrize(
        Player storage player,
        Round storage playerRound
    ) private view returns (uint256) {
        uint256 betPool = player.up_down == UP_DOWN.UP
            ? playerRound.upPrizePool
            : playerRound.downPrizePool;

        if (betPool == 0) return 0; // Avoid division by zero
        return (player.bet * playerRound.totalPrizePool) / betPool;
    }

    /**
     * @notice Receive Ether to add to the prize pool.
     */
    receive() external payable {
        addToPrizePool();
    }

    /**
     * @notice Fallback function to handle unexpected Ether transfers.
     */
    fallback() external payable {
        addToPrizePool();
    }
}
