// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * @title BitcoinUpDown
 * @dev A betting game where players predict whether the price of Bitcoin will go up or down within a round duration.
 */
contract BitcoinUpDown is Ownable, ReentrancyGuard {
    enum UP_DOWN {
        UP,
        DOWN
    }

    struct Player {
        uint256 bet;
        UP_DOWN up_down;
        uint256 round;
    }

    struct Round {
        uint256 prize_pool;
        uint256 prize_pool_up;
        uint256 prize_pool_down;
        UP_DOWN winner;
    }

    mapping(address => Player) public players_bet;
    mapping(uint256 => Round) public rounds;
    AggregatorV3Interface public immutable btc_usd_price_feed;

    uint256 public round;
    uint256 public ts_start;
    uint256 public constant ROUND_DURATION = 1 days;
    int256 public price_open;
    int256 public price_close;

    // Events
    event eventStartRound(
        uint256 indexed round,
        uint256 indexed ts_start,
        int256 price_open,
        uint256 seed
    );
    event eventBet(
        uint256 indexed round,
        address indexed player,
        uint256 amount,
        UP_DOWN up_down
    );
    event eventEndRound(
        uint256 indexed round,
        uint256 indexed ts_close,
        int256 price_close,
        uint256 prize_pool,
        uint256 prize_pool_up,
        uint256 prize_pool_down,
        UP_DOWN winner
    );
    event eventAddToPrizePool(
        uint256 indexed round,
        address indexed from,
        uint256 amount
    );

    constructor(address _price_feed_address) {
        require(
            _price_feed_address != address(0),
            "Invalid price feed address"
        );
        btc_usd_price_feed = AggregatorV3Interface(_price_feed_address);
        _startRound(0); // Initialize the first round with no seed
    }

    /**
     * @notice Get the latest price of Bitcoin in USD
     * @return The latest price in int256 format
     */
    function getPrice() public view returns (int256) {
        (, int256 price, , , ) = btc_usd_price_feed.latestRoundData();
        return price;
    }

    /**
     * @notice Adds Ether to the current round's prize pool.
     */
    function addToPrizePool() public payable {
        require(msg.value > 0, "Minimum amount to add is 1 wei");
        rounds[round].prize_pool += msg.value;
        emit eventAddToPrizePool(round, msg.sender, msg.value);
    }

    /**
     * @notice Place a bet for the current round.
     * @param _up_down The direction of the bet (UP or DOWN).
     */
    function bet(UP_DOWN _up_down) public payable {
        require(msg.value > 0, "Minimum bet is 1 wei");
        require(block.timestamp - ts_start <= 3600, "Betting is closed");

        Player storage player = players_bet[msg.sender];
        Round storage current_round = rounds[round];

        // Ensure the player has no pending bets or previous round obligations
        require(
            player.bet == 0 || player.round != round,
            "Bet already placed this round"
        );
        require(
            player.bet == 0 || player.up_down != current_round.winner,
            "Pending withdrawal from previous round"
        );

        // Update player and round data
        player.bet = msg.value;
        player.up_down = _up_down;
        player.round = round;

        current_round.prize_pool += msg.value;
        if (_up_down == UP_DOWN.UP) {
            current_round.prize_pool_up += msg.value;
        } else {
            current_round.prize_pool_down += msg.value;
        }

        emit eventBet(round, msg.sender, msg.value, _up_down);
    }

    /**
     * @notice Withdraw winnings for a player if they won in a previous round.
     */
    function withdraw() public nonReentrant {
        Player storage player = players_bet[msg.sender];
        require(player.bet > 0, "No active bets");
        require(player.round < round, "Current round still ongoing");

        Round storage player_round = rounds[player.round];
        require(player.up_down == player_round.winner, "Not a winning bet");

        uint256 prize = _calculatePrize(player, player_round);
        player.bet = 0; // Reset player bet
        (bool sent, ) = msg.sender.call{value: prize}("");
        require(sent, "Withdrawal failed");
    }

    /**
     * @notice End the current round and start a new one.
     */
    function endRound() public {
        require(
            block.timestamp - ts_start >= ROUND_DURATION,
            "Round duration is less than 24 hours"
        );

        Round storage current_round = rounds[round];
        price_close = getPrice();

        // Determine the winner
        UP_DOWN winner;
        uint256 seed = 0;

        if (price_close >= price_open) {
            winner = UP_DOWN.UP;
            if (current_round.prize_pool_up == 0) {
                seed = current_round.prize_pool; // Carry forward if no UP bets
            }
        } else {
            winner = UP_DOWN.DOWN;
            if (current_round.prize_pool_down == 0) {
                seed = current_round.prize_pool; // Carry forward if no DOWN bets
            }
        }
        current_round.winner = winner;

        emit eventEndRound(
            round,
            block.timestamp,
            price_close,
            current_round.prize_pool,
            current_round.prize_pool_up,
            current_round.prize_pool_down,
            winner
        );

        // Start a new round
        round += 1;
        _startRound(seed);
    }

    /**
     * @dev Internal function to start a new round.
     * @param seed The amount to seed the new round's prize pool.
     */
    function _startRound(uint256 seed) private {
        Round storage new_round = rounds[round];
        new_round.prize_pool = seed;
        price_open = getPrice();
        ts_start = block.timestamp;
        emit eventStartRound(round, ts_start, price_open, seed);
    }

    /**
     * @dev Internal function to calculate prize for a player.
     * @param player The player's data.
     * @param player_round The round data associated with the player.
     * @return The calculated prize amount.
     */
    function _calculatePrize(
        Player storage player,
        Round storage player_round
    ) private view returns (uint256) {
        uint256 bet_pool = player.up_down == UP_DOWN.UP
            ? player_round.prize_pool_up
            : player_round.prize_pool_down;

        if (bet_pool == 0) return 0; // Avoid division by zero
        return (player.bet * player_round.prize_pool) / bet_pool;
    }

    /**
     * @notice Receive Ether to add to the prize pool.
     */
    receive() external payable {
        addToPrizePool();
    }

    /**
     * @notice Fallback function to handle unexpected Ether transfers.
     */
    fallback() external payable {
        addToPrizePool();
    }

    /**
     * @notice Calculates the pending withdrawal amount for a player.
     * @param _addr The address of the player.
     * @return The amount in wei the player can withdraw.
     */
    function getPendingWithdraw(address _addr) public view returns (uint256) {
        Player storage player = players_bet[_addr];

        // If the player has no bet or is still in the current round, return 0
        if (player.bet == 0 || player.round == round) {
            return 0;
        }

        Round storage player_round = rounds[player.round];

        // If the player didn't bet on the winning side, return 0
        if (player.up_down != player_round.winner) {
            return 0;
        }

        // Calculate the player's share of the prize pool
        uint256 bet_pool = player.up_down == UP_DOWN.UP
            ? player_round.prize_pool_up
            : player_round.prize_pool_down;

        if (bet_pool == 0) {
            return 0; // Avoid division by zero
        }

        return (player.bet * player_round.prize_pool) / bet_pool;
    }
}
